function  onActive(slug) {
    if (slug === 'search') {
        store.commit('games/TOGGLE_SEARCH')
        if (!props.search.model.length || !props.search.active) {
            return;
        } else {
            onSearch()
            return;
        }
    }

    emit('onActive', slug)
}
function onSearch() {
    if (props.search.active && props.search.model.length) {
        emit('search_game');
    }
}
